mapper.select:
select 

from t_qm_appeal_process_record
where APPEAL_ID = #{appealId,jdbcType=VARCHAR}
  and PROCESS_ID = #{processId,jdbcType=VARCHAR}
  and NODE_ID = #{nodeId,jdbcType=INTEGER}

mapper.delete:
delete from t_qm_appeal_process_record
where APPEAL_ID = #{appealId,jdbcType=VARCHAR}
  and PROCESS_ID = #{processId,jdbcType=VARCHAR}
  and NODE_ID = #{nodeId,jdbcType=INTEGER}
