mapper.select:
select 

from t_qm_appeal_node_info
where PROCESS_ID = #{processId,jdbcType=VARCHAR}
  and NODE_ID = #{nodeId,jdbcType=INTEGER}
  and USER_ID = #{userId,jdbcType=VARCHAR}

mapper.delete:
delete from t_qm_appeal_node_info
where PROCESS_ID = #{processId,jdbcType=VARCHAR}
  and NODE_ID = #{nodeId,jdbcType=INTEGER}
  and USER_ID = #{userId,jdbcType=VARCHAR}
